import getDataByFilters from '../services/spaceX'
import Box from "@material-ui/core/Box";
import FilterCards from './filter_card'
import Cards from './cards'

//  launch_success:true,
//     land_success:false,
//     launch_year:2006

export default () => {
    console.log(getDataByFilters)

    const [filters , applyFilters] = useState({launch_success:true,land_success:false,launch_year:2006})
    const [cards , applyCards] = useState([])
    useEffect(() => {
        getDataByFilters(filters).then(response => {
            console.log(response)
            applyCards(response);
          });
      });
     const handleLaunch = value =>(e)=>{
         alert(value)
 applyFilters({launch_success:value})
     }
     const handleLand = () =>{

     }
     const handleYear =()=>{

     }
    //  const filters={
    //      launch_success:true,
    //      land_success:false,
    //      launch_year:2006,
    //  }
    return(
        <Box display="flex" p={1}>
                    <FilterCards launch_success={filters} handleLaunch={handleLaunch}
                     land_success={filters.land_success} handleLand={handleLand}
                     launch_year={filters.launch_year} handleYear={handleYear}/>
            <Cards/>
        </Box>
    )
   
}
