import React from "react";
import { flexbox } from "@material-ui/system";
import Box from "@material-ui/core/Box";
import { makeStyles } from "@material-ui/core/styles";
import CardActions from "@material-ui/core/CardActions";
import Button from "@material-ui/core/Button";
import {
  Grid,
  Card,
  CardActionArea,
  CardContent,
  Typography,
  CardHeader,
} from "@material-ui/core/";

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  button: {
    backgroundColor: "#aed581",
  },
});
const years = [{launchYear :2006}, {launchYear :2007},{launchYear :2008}, {launchYear :2009}];
export default () => {
  const classes = useStyles();
const [yearList , setYear] = React.useState(years)
const toggleYear =()=>{
  const sortYear = [...yearList].sort((a,b)=>{
return b.launchYear - a.launchYear;
  })
  setYear(sortYear) 
  alert(sortYear[1].launch_year);
}

  return (
   
      <Grid
        container
        spacing={2}
        direction="row"
        justify="flex-start"
        alignItems="flex-start"
      >
        <Card className={classes.root}>
          <CardHeader title="Filters" subheader={`Launch Year`} />

          {yearList.map((data, index) => (
            <Grid item xs={12} sm={6} md={3} key={years.indexOf(data)}>
              <CardActionArea>
               
                <Button size="small" className={classes.button} value={data} onClick={toggleYear}>
                  {data}
                </Button>
              </CardActionArea>
            </Grid>
          ))}
          <CardHeader subheader="successful Launch" />
          <CardHeader subheader="successful Landing" />
        </Card>
      </Grid>
    // </Box>
    // </div>
  );
};
