import React from "react";
import { flexbox } from "@material-ui/system";
import Box from "@material-ui/core/Box";
import { makeStyles } from "@material-ui/core/styles";
import CardActions from "@material-ui/core/CardActions";
import Button from "@material-ui/core/Button";
import {
  Grid,
  Card,
  CardActionArea,
  CardContent,
  Typography,
  CardHeader,
} from "@material-ui/core/";

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  button: {
    backgroundColor: "#aed581",
  },
});
const years = ['2006', '2007', '2008', '2009'];
export default () => {
  const classes = useStyles();
const [year , setYear] = React.useState('2005')
const toggleYear =(event)=>{
   const newyear = event.target.value
   alert(year) 
   alert(newyear) 
       setYear(newyear)
   
    // setYear(year === newyear)
     
}
alert(year)
  return (
   
      <Grid
        container
        spacing={2}
        direction="row"
        justify="flex-start"
        alignItems="flex-start"
      >
        <Card className={classes.root}>
          <CardHeader title="Filters" subheader={`Launch Year`} />

          {years.map((data, index) => (
            <Grid item xs={12} sm={6} md={3} key={years.indexOf(data)}>
              <CardActionArea>
               
                <Button size="small" className={classes.button} value={data} onChange={toggleYear}>
                  {data}
                </Button>
              </CardActionArea>
            </Grid>
          ))}
          <CardHeader subheader="successful Launch" />
          <CardHeader subheader="successful Landing" />
        </Card>
      </Grid>
    // </Box>
    // </div>
  );
};
