import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import {
    Grid,
    Card,
    CardContent,
    Typography,
    CardHeader
} from '@material-ui/core/'

const useStyles = makeStyles({
    root: {
      maxWidth: 345,
    },
  });
  const statData = [
    
    {
      title: "SAMPLE1",
      description: "Sample",
      image: require("../images/image_1.jpg"),
    },
    {
        title: "SAMPLE2",
        description: "Sample",
        image: require("../images/image_2.png"),
      },
      {
        title: "SAMPLE3",
        description: "Sample",
        image: require("../images/image_3.png"),
      },
      {
        title: "SAMPLE4",
        description: "Sample",
        image: require("../images/image_4.jpg"),
      },
      {
        title: "SAMPLE5",
        description: "Sample",
        image: require("../images/image_5.png"),
      },
      {
        title: "SAMPLE6",
        description: "Sample",
        image: require("../images/image_6.jpg"),
      },
  ];
export default ()=>{
    const classes = useStyles();
    // const settings = {
    //     slidesToShow: 3,
    //     cssEase: "linear",
    //   };
    return(
        <Grid
                container
                spacing={2}
                direction="row"
                justify="flex-start"
                alignItems="flex-start"
            >
        {statData.map((cards, index) => (
            <Grid item xs={12} sm={6} md={3} key={statData.indexOf(cards)}>
        <Card className={classes.root} >
       <CardActionArea>
        <CardMedia
          component="img"
          alt="image"
          height="140"
          image={cards.image}
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography variant="body2" color="textSecondary" component="p">
            Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
            across all continents except Antarctica
          </Typography>
        </CardContent>
      </CardActionArea>
      
    </Card>
    </Grid>
        ))}
    </Grid>
    )
}
